const axios = require('axios');
const sequelize = require();

function searchBreweriesByZip(zipcode) {
    return axios
        .get(`https://api.openbrewerydb.org/breweries?by_postal=${zipcode}&per_page=5`)
        .then(response => {
            const data = response.data;
            data.forEach(brewery => {
                sequelize.query(`
                  INSERT INTO breweries (name, brewery_type, street, city, state, phone, website_url)
                  VALUES ('${brewery.name}', '${brewery.brewery_type}', '${brewery.street}', '${brewery.city}', '${brewery.state}', ${brewery.phone}, '${brewery.website_url}')
                `);
            });
        });
}
module.exports = {
    searchBreweriesByZip
}
