import React, { useState } from 'react';
import { useEffect } from 'react';
import axios from 'axios';
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';

function Home() {
  const [zipCode, setZipCode] = useState('');
  const [barList, setBarList] = useState([]);

  useEffect(() => {
    if (zipCode !== "") {
      axios
        .get(`https://www.openbrewerydb.org/breweries?by_postal=${zipCode}`)
        .then((res) => setBarList(res.data));
    }
  }, [zipCode]);

  function handleSubmit(event) {
    event.preventDefault();
  }

  return (
    <div style={{ backgroundImage: `url(https://images.unsplash.com/photo-1597290282695-edc43d0e7129?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1475&q=80)`}}>
    <div>
      <h1>Welcome to our BarCrawl App</h1>
      <nav>
        <ul>
          <li>
            <a href="#">Home</a>
          </li>
          <li>
            <a href="#">About</a>
          </li>
        </ul>
      </nav>
      <form onSubmit={handleSubmit}>
        <label htmlFor="search-bar">Search for Bars in Zip Code:</label>
        <input
          type="text"
          id="search-bar"
          name="search-bar"
          value={zipCode}
          onChange={(event) => setZipCode(event.target.value)}
        />
      </form>
      <ul>
        {barList.map((bar) => (
          <li key={bar.id}>{bar.name}</li>
        ))}
      </ul>
      <nav>
        <ul>
          <li>
            <LoginForm />
          </li>
          <li>
            <SignupForm />
          </li>
        </ul>
      </nav>
      </div>
    </div>
  );
}

export default Home;
