import React, { useState } from 'react';
import SearchBar from "./SearchBar";
import { searchBreweriesByZip } from "./Api";

function Home() {
  const [breweries, setBreweries] = useState([]);
  console.log("Breweries:", breweries);

  // Helper function to get the value of a cookie
  function getCookie(name) {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
  }

  // Check if the token is present in the cookies
  const token = getCookie('token');
  let loggedInMessage;
  if (token) {
    loggedInMessage = <p>You are logged in!</p>;
  }

  function handleSearch(zipcode) {
    searchBreweriesByZip(zipcode)
      .then(data => {
        console.log("Search Results: ", data);
        setBreweries(data.slice(0,5));
      });
  }

  return (
    <div className='container'>
      <h1 className='Header'>Welcome to our BarCrawl App</h1>
      <div className='NavbarContainer'>
        <nav className='Navbar'>
          <ul>
            <li>
              <a href="/">Home</a>
            </li>
            <li>
              <a href="/#">About</a>
            </li>
            <li>
              <a href="/login">Login</a>
            </li>
            <li>
              <a href="signup">Signup</a>
            </li>
          </ul>
        </nav>
      </div>
      <div className='SearchbarContainer'> 
        <div className="Searchbar">
          <SearchBar onSearch={handleSearch} />
          {breweries.length > 0 && 
            <div className="resultsContainer">
              {breweries.map((brewery, index) => (
                <div className="card" key={index}>
                  <h2>{brewery.name}</h2>
                  <p>{brewery.address}</p>
                  <p>{brewery.phone}</p>
                  <a href={brewery.website_url}>{brewery.website_url}</a>
                </div>
              ))}
            </div>
          }   
        </div>
      </div>
      {loggedInMessage}
    </div>
  );
}

export default Home;
