import React, { useState } from 'react';

function Home() {
  const [zipCode, setZipCode] = useState('');
  const [barList, setBarList] = useState([]);
  

  async function fetchBarList(zipCode) {
    
    try {
      const response = await fetch(`https://www.openbrewerydb.org/breweries?by_postal=${zipCode}`);
      const data = await response.json();
      setBarList(data);
      
    } catch (error) {
      console.error(error);
      
    }
  }

  function handleSubmit(event) {
    event.preventDefault();
    fetchBarList(zipCode);
  }

  return (
    <div className='background_image' style={{ backgroundImage: `url(https://images.unsplash.com/photo-1597290282695-edc43d0e7129?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1475&q=80)`}}>
    <div>
      <h1 className='header'>Welcome to our BarCrawl App</h1>
      <nav>
        <ul>
          <li>
            <a href="/home">Home</a>
          </li>
          <li>
            <a href="/#">About</a>
          </li>
          <li>
            <a href="/login">Login</a>
          </li>
          <li>
            <a href="signup">Signup</a>
          </li>
        </ul>
      </nav>
      
      <form onSubmit={handleSubmit}>
        <label htmlFor="search-bar">Search for Bars in Zip Code:</label>
        <input
          type="text"
          id="search-bar"
          name="search-bar"
          value={zipCode}
          onChange={(event) => setZipCode(event.target.value)}
        />
      </form>
       <ul>
        {barList.map((bar) => (
          <li key={bar.id}>{bar.name}</li>
        ))}
      </ul>
      
      </div>
    </div>
  );
}

export default Home;
